export default class Tool {
    canvas: any
    ctx: any 
    constructor(canvas: HTMLCanvasElement | null) {
        this.canvas = canvas
        this.imgReturn()
        if (canvas) this.ctx = canvas.getContext('2d')

        this.destroyEvents()
    }

    set fillColor(color: string) {
        this.ctx.fillStyle = color
    }

    set strokeColor(color: string) {
        this.ctx.strokeStyle = color
    }

    set lineWidth(width: string) {
        this.ctx.lineWidth = width
    }

    imgReturn() {
        const canvasElement = document.createElement('canvas');
        canvasElement.width = 1000;
        canvasElement.height = 600;
        const canvasDataURL = this.canvas.toDataURL()
        const img = new Image()
        img.onload = () => {
            const context = canvasElement.getContext('2d');
            if (context) {
            context.drawImage(img, 0, 0, canvasElement.width, canvasElement.height);
            const canvasDataURL = canvasElement.toDataURL();
            img.src = canvasDataURL;
            this.canvas = img.src;
            } else {
            console.error("getContext('2d') returned null");
            }
        }
        img.src = canvasDataURL
        this.canvas = img.src
    }

    destroyEvents() {
        if (this.canvas) {
            this.canvas.onmousemove = null
            this.canvas.onmousedown = null
            this.canvas.onmouseup = null
        }
    }
}
