/* eslint-disable react-hooks/exhaustive-deps */
import React, {FC, useEffect, useRef, useState} from 'react';
import style from '../styles/canvas.module.scss'
import {useAppDispatch, useAppSelector} from '../hooks/redux';
import {canvasSlice} from '../store/reducers/CanvasSlice';
import {toolSlice} from '../store/reducers/ToolSlice';
import Brush from '../tools/Brush';
import {Button, Form, InputGroup, Modal} from "react-bootstrap";
import {useLocation, useParams} from "react-router-dom";

const Canvas: FC = () => {
      const {setCanvas, pushToUndo, setUsername} = canvasSlice.actions
      const {setTool} = toolSlice.actions
      const {username} = useAppSelector(state => state.canvasReducer)
      const dispatch = useAppDispatch()
      const [modal, setModal] = useState(true)
      const [activeButton, setActiveButton] = useState('')
      const canvasRef = useRef<HTMLCanvasElement | null>(null)
      const usernameRef = useRef<HTMLInputElement | null>(null)
      const params= useParams()

      useEffect(() => {
            // const canvasGetContext = canvasRef.current?.getContext('2d')
            // const contextToDataURL = canvasRef.current?.toDataURL()
            if (canvasRef.current) {
                  dispatch(setCanvas(canvasRef.current))
                  dispatch(setTool(new Brush(canvasRef.current)))
            }      
      }, [])

      useEffect(() => {
            if (username) {
                  const socket = new WebSocket('ws://localhost:5001')
                  socket.onopen = () => {
                        console.log('lalalalal')
                        socket.send(JSON.stringify({
                              id: params.id,
                              username,
                              method: 'connection'
                        }))
                  }
            }
      }, [username])

      const mouseDownHandler = () => {
            dispatch(pushToUndo(canvasRef.current?.toDataURL()))
      }

      const connectHandler = () => {
            if (usernameRef.current?.value) {
                  dispatch(setUsername(usernameRef.current?.value))
                  setModal(false)
            }
      }

      // useEffect(() => {
      //       console.log(usernameRef.current?.value)
      // }, [usernameRef.current?.value])
      console.log(usernameRef.current?.value)

      return (
            <div className={style.canvas}>
                  <Modal show={modal} onHide={() => {}}>
                        <Modal.Header>
                              <Modal.Title>Enter you name</Modal.Title>
                        </Modal.Header>
                        <Modal.Body>
                              <InputGroup className="mb-3">
                                    <Form.Control
                                        placeholder="Username"
                                        ref={usernameRef}
                                        //value={activeButton}
                                        onChange={(e) => setActiveButton(e.target.value)}
                                    />
                              </InputGroup>
                        </Modal.Body>
                        <Modal.Footer>
                              <Button
                                  variant={usernameRef.current?.value ? "primary" : 'secondary'}
                                  onClick={() => connectHandler()}
                              >
                                    Login
                              </Button>
                        </Modal.Footer>
                  </Modal>
                  <canvas 
                        id='canvas'
                        onMouseDown={() => mouseDownHandler()} 
                        ref={canvasRef} 
                        width={1000} 
                        height={600} 
                  />
            </div>
      );
};

export default Canvas;
