import {combineReducers, configureStore} from "@reduxjs/toolkit";
import toolReducer from './reducers/ToolSlice';
import canvasReducer from './reducers/CanvasSlice';
import { canvasRefMiddleware } from "./midl";

const rootReducer = combineReducers({
    toolReducer,
    canvasReducer
})
const canvasRefMiddlewareWithExtraData = canvasRefMiddleware(canvasRef);

export const setupStore = () => {
    return configureStore({
        reducer: rootReducer,
        middleware: (getDefaultMiddleware) =>
    getDefaultMiddleware().concat(canvasRefMiddlewareWithExtraData)
    })
}

export type RootState = ReturnType<typeof rootReducer>
export type AppStore = ReturnType<typeof setupStore>
export type AppDispatch = AppStore['dispatch']
