export default class Tool {
    canvas: any
    ctx: any
    constructor(canvas: any) {
        this.canvas = canvas
        this.imgReturn()
        this.ctx = canvas.getContext('2d')
        this.destroyEvents()
    }

    imgReturn() {
        const img = new Image()
        img.src = this.canvas
        this.canvas
    }

    destroyEvents() {
        this.canvas.onmousemove = null
        this.canvas.onmousedown = null
        this.canvas.onmouseup = null
    }
}
