import {createSlice, PayloadAction} from "@reduxjs/toolkit";

interface CanvasState {
      canvas: HTMLCanvasElement | null;
      undoList: any;
      redoList: any
}

const initialState: CanvasState = {
      canvas: null,
      undoList: [],
      redoList: []

}

export const canvasSlice = createSlice({
    name: 'canvas',
    initialState,
    reducers: {
      setCanvas(state, action: PayloadAction<any>){						
            state.canvas = action.payload
      },
      pushToUndo(state, action: PayloadAction<any>) {
            state.undoList.push(action.payload)
      },
      pushToRedo(state, action: PayloadAction<any>) {
            state.redoList.push(action.payload)
      },
      undo(state) {
            let ctx = state.canvas?.getContext('2d');
    if (state.undoList.length > 0) {
        state.undoList.pop(); // Видалити останній елемент з масиву
        if(state.canvas) ctx?.clearRect(0, 0, state.canvas.width, state.canvas.height);

        // Оновити малюнок на canvas на основі залишених даних у масиві
        state.undoList.forEach((dataURL) => {
            let img = new Image();
            img.src = dataURL;
            img.onload = () => {
                if (state.canvas) {
                    ctx?.drawImage(img, 0, 0, state.canvas.width, state.canvas.height);
                }
            };
        });
            // let ctx = state.canvas?.getContext('2d')
            // if (state.undoList.length > 0) {
            //       let dataURL = state.undoList.pop()
            //       let img = new Image()
            //       img.src = dataURL
            //       img.onload = () => {
            //             console.log('fuck')
            //             // if (state.canvas) {
            //             //       ctx?.clearRect(0, 0, state.canvas.width, state.canvas.height)
            //             //       ctx?.drawImage(img, 0, 0, state.canvas.width, state.canvas.height)
            //             // }
            //       }
            } else {
                  if(state.canvas) ctx?.clearRect(0, 0, state.canvas.width, state.canvas.height)
            }
      }
    }
})

export default canvasSlice.reducer
